@require_GET
def city_suggestions(request):
    query = request.GET.get('term', '')
    if query:
        response = requests.get(f'http://api.geonames.org/searchJSON?name_startsWith={query}&maxRows=10&username=abdulazizov')
        results = response.json().get('geonames', [])
        suggestions = list({city['name'] for city in results})  # Ensure unique city names
    else:
        suggestions = []
    return JsonResponse(suggestions, safe=False)

def index(request):
    weather_data = None
    city = ''
    
    if request.method == 'POST':
        city = request.POST.get('city')
        
        # Get the city's coordinates from GeoNames API
        geo_response = requests.get(f'http://api.geonames.org/searchJSON?q={city}&maxRows=1&username=abdulazizov')
        geo_data = geo_response.json().get('geonames', [])
        
        if geo_data:
            lat = geo_data[0]['lat']
            lon = geo_data[0]['lng']
            
            # Get weather information from Open-Meteo API
            weather_response = requests.get(
                f'https://api.open-meteo.com/v1/forecast?latitude={lat}&longitude={lon}'
                f'&current_weather=true'
                f'&daily=sunrise,sunset'
                f'&timezone=GMT'
            )
            weather_data = weather_response.json()
            
            # Store the city in session
            request.session['last_searched_city'] = city
            return redirect("index")
            
    else:
        # Retrieve the last searched city from session
        city = request.session.get('last_searched_city', '')
        if city:
            # Get the city's coordinates from GeoNames API
            geo_response = requests.get(f'http://api.geonames.org/searchJSON?q={city}&maxRows=1&username=abdulazizov')
            geo_data = geo_response.json().get('geonames', [])
            
            if geo_data:
                lat = geo_data[0]['lat']
                lon = geo_data[0]['lng']
                
                # Get weather information from Open-Meteo API
                weather_response = requests.get(
                    f'https://api.open-meteo.com/v1/forecast?latitude={lat}&longitude={lon}'
                    f'&current=temperature_2m,relative_humidity_2m,apparent_temperature,is_day,precipitation,rain,showers,snowfall,weather_code,cloud_cover,pressure_msl,surface_pressure,wind_speed_10m,wind_direction_10m'
                    f'&daily=sunrise,sunset'
                    f'&timezone=GMT'
                )
                weather_data = weather_response.json()
    
    # Format weather data for the template
    formatted_weather_data = None
    if weather_data:
        print(weather_data)
        current = weather_data.get('current', {})
        daily = weather_data.get('daily', {})
        sunrise = daily.get('sunrise', [None])[0]
        sunset = daily.get('sunset', [None])[0]

        # Format sunrise and sunset to display only time
        formatted_sunrise = datetime.fromisoformat(sunrise).strftime('%H:%M') if sunrise else None
        formatted_sunset = datetime.fromisoformat(sunset).strftime('%H:%M') if sunset else None

        # Get the weather image URL
        weather_code = current.get('weathercode', None)
        weather_image_url = weather_images.get(weather_code, "https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-weather/ilu1.webp")  # Default image if code not found

        formatted_weather_data = {
            'current': {
                'temperature': current.get('temperature_2m', None),
                'relative_humidity': current.get('relative_humidity_2m', None),
                'apparent_temperature': current.get('apparent_temperature', None),
                'is_day': current.get('is_day', None),
                'precipitation': current.get('precipitation', None),
                'rain': current.get('rain', None),
                'showers': current.get('showers', None),
                'snowfall': current.get('snowfall', None),
                'weather_code': weather_code,
                'cloud_cover': current.get('cloud_cover', None),
                'pressure_msl': current.get('pressure_msl', None),
                'surface_pressure': current.get('surface_pressure', None),
                'wind_speed_10m': current.get('wind_speed_10m', None),
                'wind_direction_10m': current.get('winddirection', None),
                'weather_image_url': weather_image_url, 
            },
            'daily': {
                'sunrise': formatted_sunrise,
                'sunset': formatted_sunset,
            }
        }
    
    return render(request, 'index.html', {'weather_data': formatted_weather_data, 'city': city})
